mapper.update:
UPDATE b3_solo_archivedate_article SET 
	archiveDate_oId = #{archivedateOid,jdbcType=VARCHAR}
	,article_oId = #{articleOid,jdbcType=VARCHAR}
WHERE oId = #{oid,jdbcType=VARCHAR}

mapper.select:
SELECT

FROM b3_solo_archivedate_article


		AND oId = #{oid,jdbcType=VARCHAR}


		AND archiveDate_oId = #{archivedateOid,jdbcType=VARCHAR}


		AND article_oId = #{articleOid,jdbcType=VARCHAR}


ORDER BY oId DESC

	LIMIT #{start} ,#{offset}

SELECT COUNT(*) FROM b3_solo_archivedate_article


		AND oId = #{oid,jdbcType=VARCHAR}


		AND archiveDate_oId = #{archivedateOid,jdbcType=VARCHAR}


		AND article_oId = #{articleOid,jdbcType=VARCHAR}
