mapper.update:
UPDATE b3_solo_archivedate SET 
	archiveDateArticleCount = #{archivedatearticlecount,jdbcType=INTEGER}
	,archiveDatePublishedArticleCount = #{archivedatepublishedarticlecount,jdbcType=INTEGER}
	,archiveTime = #{archivetime,jdbcType=BIGINT}
WHERE oId = #{oid,jdbcType=VARCHAR}

mapper.select:
SELECT

FROM b3_solo_archivedate


		AND oId = #{oid,jdbcType=VARCHAR}


		AND archiveDateArticleCount = #{archivedatearticlecount,jdbcType=INTEGER}


		AND archiveDatePublishedArticleCount = #{archivedatepublishedarticlecount,jdbcType=INTEGER}


		AND archiveTime = #{archivetime,jdbcType=BIGINT}


ORDER BY oId DESC

	LIMIT #{start} ,#{offset}

SELECT COUNT(*) FROM b3_solo_archivedate


		AND oId = #{oid,jdbcType=VARCHAR}


		AND archiveDateArticleCount = #{archivedatearticlecount,jdbcType=INTEGER}


		AND archiveDatePublishedArticleCount = #{archivedatepublishedarticlecount,jdbcType=INTEGER}


		AND archiveTime = #{archivetime,jdbcType=BIGINT}
