mapper.update:
UPDATE b3_solo_article SET 
	articleTitle = #{articletitle,jdbcType=VARCHAR}
	,articleAbstract = #{articleabstract,jdbcType=LONGVARCHAR}
	,articleTags = #{articletags,jdbcType=LONGVARCHAR}
	,articleAuthorEmail = #{articleauthoremail,jdbcType=VARCHAR}
	,articleCommentCount = #{articlecommentcount,jdbcType=INTEGER}
	,articleViewCount = #{articleviewcount,jdbcType=INTEGER}
	,articleContent = #{articlecontent,jdbcType=LONGVARCHAR}
	,articlePermalink = #{articlepermalink,jdbcType=VARCHAR}
	,articleHadBeenPublished = #{articlehadbeenpublished,jdbcType=CHAR}
	,articleIsPublished = #{articleispublished,jdbcType=CHAR}
	,articlePutTop = #{articleputtop,jdbcType=CHAR}
	,articleCreateDate = #{articlecreatedate,jdbcType=TIMESTAMP}
	,articleUpdateDate = #{articleupdatedate,jdbcType=TIMESTAMP}
	,articleRandomDouble = #{articlerandomdouble,jdbcType=DOUBLE}
	,articleSignId = #{articlesignid,jdbcType=VARCHAR}
	,articleCommentable = #{articlecommentable,jdbcType=CHAR}
	,articleViewPwd = #{articleviewpwd,jdbcType=VARCHAR}
	,articleEditorType = #{articleeditortype,jdbcType=VARCHAR}
WHERE oId = #{oid,jdbcType=VARCHAR}

mapper.select:
SELECT

FROM b3_solo_article


		AND oId = #{oid,jdbcType=VARCHAR}


		AND articleTitle = #{articletitle,jdbcType=VARCHAR}


		AND articleAbstract = #{articleabstract,jdbcType=LONGVARCHAR}


		AND articleTags = #{articletags,jdbcType=LONGVARCHAR}


		AND articleAuthorEmail = #{articleauthoremail,jdbcType=VARCHAR}


		AND articleCommentCount = #{articlecommentcount,jdbcType=INTEGER}


		AND articleViewCount = #{articleviewcount,jdbcType=INTEGER}


		AND articleContent = #{articlecontent,jdbcType=LONGVARCHAR}


		AND articlePermalink = #{articlepermalink,jdbcType=VARCHAR}


		AND articleHadBeenPublished = #{articlehadbeenpublished,jdbcType=CHAR}


		AND articleIsPublished = #{articleispublished,jdbcType=CHAR}


		AND articlePutTop = #{articleputtop,jdbcType=CHAR}


		AND articleCreateDate = #{articlecreatedate,jdbcType=TIMESTAMP}


		AND articleUpdateDate = #{articleupdatedate,jdbcType=TIMESTAMP}


		AND articleRandomDouble = #{articlerandomdouble,jdbcType=DOUBLE}


		AND articleSignId = #{articlesignid,jdbcType=VARCHAR}


		AND articleCommentable = #{articlecommentable,jdbcType=CHAR}


		AND articleViewPwd = #{articleviewpwd,jdbcType=VARCHAR}


		AND articleEditorType = #{articleeditortype,jdbcType=VARCHAR}


ORDER BY oId DESC

	LIMIT #{start} ,#{offset}

SELECT COUNT(*) FROM b3_solo_article


		AND oId = #{oid,jdbcType=VARCHAR}


		AND articleTitle = #{articletitle,jdbcType=VARCHAR}


		AND articleAbstract = #{articleabstract,jdbcType=LONGVARCHAR}


		AND articleTags = #{articletags,jdbcType=LONGVARCHAR}


		AND articleAuthorEmail = #{articleauthoremail,jdbcType=VARCHAR}


		AND articleCommentCount = #{articlecommentcount,jdbcType=INTEGER}


		AND articleViewCount = #{articleviewcount,jdbcType=INTEGER}


		AND articleContent = #{articlecontent,jdbcType=LONGVARCHAR}


		AND articlePermalink = #{articlepermalink,jdbcType=VARCHAR}


		AND articleHadBeenPublished = #{articlehadbeenpublished,jdbcType=CHAR}


		AND articleIsPublished = #{articleispublished,jdbcType=CHAR}


		AND articlePutTop = #{articleputtop,jdbcType=CHAR}


		AND articleCreateDate = #{articlecreatedate,jdbcType=TIMESTAMP}


		AND articleUpdateDate = #{articleupdatedate,jdbcType=TIMESTAMP}


		AND articleRandomDouble = #{articlerandomdouble,jdbcType=DOUBLE}


		AND articleSignId = #{articlesignid,jdbcType=VARCHAR}


		AND articleCommentable = #{articlecommentable,jdbcType=CHAR}


