mapper.update:
UPDATE b3_solo_category SET 
	categoryTitle = #{categorytitle,jdbcType=VARCHAR}
	,categoryURI = #{categoryuri,jdbcType=VARCHAR}
	,categoryDescription = #{categorydescription,jdbcType=LONGVARCHAR}
	,categoryOrder = #{categoryorder,jdbcType=INTEGER}
	,categoryTagCnt = #{categorytagcnt,jdbcType=INTEGER}
WHERE oId = #{oid,jdbcType=VARCHAR}

mapper.select:
SELECT

FROM b3_solo_category


		AND oId = #{oid,jdbcType=VARCHAR}


		AND categoryTitle = #{categorytitle,jdbcType=VARCHAR}


		AND categoryURI = #{categoryuri,jdbcType=VARCHAR}


		AND categoryDescription = #{categorydescription,jdbcType=LONGVARCHAR}


		AND categoryOrder = #{categoryorder,jdbcType=INTEGER}


		AND categoryTagCnt = #{categorytagcnt,jdbcType=INTEGER}


ORDER BY oId DESC

	LIMIT #{start} ,#{offset}

SELECT COUNT(*) FROM b3_solo_category


		AND oId = #{oid,jdbcType=VARCHAR}


		AND categoryTitle = #{categorytitle,jdbcType=VARCHAR}


		AND categoryURI = #{categoryuri,jdbcType=VARCHAR}


		AND categoryDescription = #{categorydescription,jdbcType=LONGVARCHAR}


		AND categoryOrder = #{categoryorder,jdbcType=INTEGER}


		AND categoryTagCnt = #{categorytagcnt,jdbcType=INTEGER}
