mapper.update:
UPDATE b3_solo_comment SET 
	commentContent = #{commentcontent,jdbcType=LONGVARCHAR}
	,commentDate = #{commentdate,jdbcType=TIMESTAMP}
	,commentEmail = #{commentemail,jdbcType=VARCHAR}
	,commentName = #{commentname,jdbcType=VARCHAR}
	,commentOnId = #{commentonid,jdbcType=VARCHAR}
	,commentOnType = #{commentontype,jdbcType=VARCHAR}
	,commentSharpURL = #{commentsharpurl,jdbcType=VARCHAR}
	,commentThumbnailURL = #{commentthumbnailurl,jdbcType=LONGVARCHAR}
	,commentURL = #{commenturl,jdbcType=VARCHAR}
	,commentOriginalCommentId = #{commentoriginalcommentid,jdbcType=VARCHAR}
	,commentOriginalCommentName = #{commentoriginalcommentname,jdbcType=VARCHAR}
WHERE oId = #{oid,jdbcType=VARCHAR}

mapper.select:
SELECT

FROM b3_solo_comment


		AND oId = #{oid,jdbcType=VARCHAR}


		AND commentContent = #{commentcontent,jdbcType=LONGVARCHAR}


		AND commentDate = #{commentdate,jdbcType=TIMESTAMP}


		AND commentEmail = #{commentemail,jdbcType=VARCHAR}


		AND commentName = #{commentname,jdbcType=VARCHAR}


		AND commentOnId = #{commentonid,jdbcType=VARCHAR}


		AND commentOnType = #{commentontype,jdbcType=VARCHAR}


		AND commentSharpURL = #{commentsharpurl,jdbcType=VARCHAR}


		AND commentThumbnailURL = #{commentthumbnailurl,jdbcType=LONGVARCHAR}


		AND commentURL = #{commenturl,jdbcType=VARCHAR}


		AND commentOriginalCommentId = #{commentoriginalcommentid,jdbcType=VARCHAR}


		AND commentOriginalCommentName = #{commentoriginalcommentname,jdbcType=VARCHAR}


ORDER BY oId DESC

	LIMIT #{start} ,#{offset}

SELECT COUNT(*) FROM b3_solo_comment


		AND oId = #{oid,jdbcType=VARCHAR}


		AND commentContent = #{commentcontent,jdbcType=LONGVARCHAR}


		AND commentDate = #{commentdate,jdbcType=TIMESTAMP}


		AND commentEmail = #{commentemail,jdbcType=VARCHAR}


		AND commentName = #{commentname,jdbcType=VARCHAR}


		AND commentOnId = #{commentonid,jdbcType=VARCHAR}


		AND commentOnType = #{commentontype,jdbcType=VARCHAR}


		AND commentSharpURL = #{commentsharpurl,jdbcType=VARCHAR}


		AND commentThumbnailURL = #{commentthumbnailurl,jdbcType=LONGVARCHAR}


		AND commentURL = #{commenturl,jdbcType=VARCHAR}


