mapper.update:
UPDATE b3_solo_option SET 
	optionValue = #{optionvalue,jdbcType=LONGVARCHAR}
	,optionCategory = #{optioncategory,jdbcType=VARCHAR}
WHERE oId = #{oid,jdbcType=VARCHAR}

mapper.select:
SELECT

FROM b3_solo_option


		AND oId = #{oid,jdbcType=VARCHAR}


		AND optionValue = #{optionvalue,jdbcType=LONGVARCHAR}


		AND optionCategory = #{optioncategory,jdbcType=VARCHAR}


ORDER BY oId DESC

	LIMIT #{start} ,#{offset}

SELECT COUNT(*) FROM b3_solo_option


		AND oId = #{oid,jdbcType=VARCHAR}


		AND optionValue = #{optionvalue,jdbcType=LONGVARCHAR}


		AND optionCategory = #{optioncategory,jdbcType=VARCHAR}
