mapper.update:
UPDATE b3_solo_page SET 
	pageCommentCount = #{pagecommentcount,jdbcType=INTEGER}
	,pageContent = #{pagecontent,jdbcType=LONGVARCHAR}
	,pageOrder = #{pageorder,jdbcType=INTEGER}
	,pagePermalink = #{pagepermalink,jdbcType=VARCHAR}
	,pageTitle = #{pagetitle,jdbcType=VARCHAR}
	,pageCommentable = #{pagecommentable,jdbcType=CHAR}
	,pageType = #{pagetype,jdbcType=VARCHAR}
	,pageOpenTarget = #{pageopentarget,jdbcType=VARCHAR}
	,pageEditorType = #{pageeditortype,jdbcType=VARCHAR}
	,pageIcon = #{pageicon,jdbcType=VARCHAR}
WHERE oId = #{oid,jdbcType=VARCHAR}

mapper.select:
SELECT

FROM b3_solo_page


		AND oId = #{oid,jdbcType=VARCHAR}


		AND pageCommentCount = #{pagecommentcount,jdbcType=INTEGER}


		AND pageContent = #{pagecontent,jdbcType=LONGVARCHAR}


		AND pageOrder = #{pageorder,jdbcType=INTEGER}


		AND pagePermalink = #{pagepermalink,jdbcType=VARCHAR}


		AND pageTitle = #{pagetitle,jdbcType=VARCHAR}


		AND pageCommentable = #{pagecommentable,jdbcType=CHAR}


		AND pageType = #{pagetype,jdbcType=VARCHAR}


		AND pageOpenTarget = #{pageopentarget,jdbcType=VARCHAR}


		AND pageEditorType = #{pageeditortype,jdbcType=VARCHAR}


		AND pageIcon = #{pageicon,jdbcType=VARCHAR}


ORDER BY oId DESC

	LIMIT #{start} ,#{offset}

SELECT COUNT(*) FROM b3_solo_page


		AND oId = #{oid,jdbcType=VARCHAR}


		AND pageCommentCount = #{pagecommentcount,jdbcType=INTEGER}


		AND pageContent = #{pagecontent,jdbcType=LONGVARCHAR}


		AND pageOrder = #{pageorder,jdbcType=INTEGER}


		AND pagePermalink = #{pagepermalink,jdbcType=VARCHAR}


		AND pageTitle = #{pagetitle,jdbcType=VARCHAR}


		AND pageCommentable = #{pagecommentable,jdbcType=CHAR}


		AND pageType = #{pagetype,jdbcType=VARCHAR}


		AND pageOpenTarget = #{pageopentarget,jdbcType=VARCHAR}


		AND pageEditorType = #{pageeditortype,jdbcType=VARCHAR}


		AND pageIcon = #{pageicon,jdbcType=VARCHAR}
