mapper.update:
UPDATE b3_solo_plugin SET 
	author = #{author,jdbcType=LONGVARCHAR}
	,name = #{name,jdbcType=VARCHAR}
	,status = #{status,jdbcType=VARCHAR}
	,version = #{version,jdbcType=VARCHAR}
	,setting = #{setting,jdbcType=LONGVARCHAR}
WHERE oId = #{oid,jdbcType=VARCHAR}

mapper.select:
SELECT

FROM b3_solo_plugin


		AND oId = #{oid,jdbcType=VARCHAR}


		AND author = #{author,jdbcType=LONGVARCHAR}


		AND name = #{name,jdbcType=VARCHAR}


		AND status = #{status,jdbcType=VARCHAR}


		AND version = #{version,jdbcType=VARCHAR}


		AND setting = #{setting,jdbcType=LONGVARCHAR}


ORDER BY oId DESC

	LIMIT #{start} ,#{offset}

SELECT COUNT(*) FROM b3_solo_plugin


		AND oId = #{oid,jdbcType=VARCHAR}


		AND author = #{author,jdbcType=LONGVARCHAR}


		AND name = #{name,jdbcType=VARCHAR}


		AND status = #{status,jdbcType=VARCHAR}


		AND version = #{version,jdbcType=VARCHAR}


		AND setting = #{setting,jdbcType=LONGVARCHAR}
