mapper.update:
UPDATE b3_solo_tag_article SET 
	article_oId = #{articleOid,jdbcType=VARCHAR}
	,tag_oId = #{tagOid,jdbcType=VARCHAR}
WHERE oId = #{oid,jdbcType=VARCHAR}

mapper.select:
SELECT

FROM b3_solo_tag_article


		AND oId = #{oid,jdbcType=VARCHAR}


		AND article_oId = #{articleOid,jdbcType=VARCHAR}


		AND tag_oId = #{tagOid,jdbcType=VARCHAR}


ORDER BY oId DESC

	LIMIT #{start} ,#{offset}

SELECT COUNT(*) FROM b3_solo_tag_article


		AND oId = #{oid,jdbcType=VARCHAR}


		AND article_oId = #{articleOid,jdbcType=VARCHAR}


		AND tag_oId = #{tagOid,jdbcType=VARCHAR}
