mapper.update:
UPDATE b3_solo_tag SET 
	tagPublishedRefCount = #{tagpublishedrefcount,jdbcType=INTEGER}
	,tagReferenceCount = #{tagreferencecount,jdbcType=INTEGER}
	,tagTitle = #{tagtitle,jdbcType=VARCHAR}
WHERE oId = #{oid,jdbcType=VARCHAR}

mapper.select:
SELECT

FROM b3_solo_tag


		AND oId = #{oid,jdbcType=VARCHAR}


		AND tagPublishedRefCount = #{tagpublishedrefcount,jdbcType=INTEGER}


		AND tagReferenceCount = #{tagreferencecount,jdbcType=INTEGER}


		AND tagTitle = #{tagtitle,jdbcType=VARCHAR}


ORDER BY oId DESC

	LIMIT #{start} ,#{offset}

SELECT COUNT(*) FROM b3_solo_tag


		AND oId = #{oid,jdbcType=VARCHAR}


		AND tagPublishedRefCount = #{tagpublishedrefcount,jdbcType=INTEGER}


		AND tagReferenceCount = #{tagreferencecount,jdbcType=INTEGER}


		AND tagTitle = #{tagtitle,jdbcType=VARCHAR}
