mapper.select:
SELECT

FROM bless_ext_userinfo


		AND id = #{id,jdbcType=BIGINT}


ORDER BY id DESC

	LIMIT #{start} ,#{offset}

mapper.update:
UPDATE bless_ext_userinfo SET 
	user_code = #{userCode,jdbcType=BIGINT}
	,org_id = #{orgId,jdbcType=BIGINT}
	,company_id = #{companyId,jdbcType=BIGINT}
	,birthday_calender = #{birthdayCalender,jdbcType=INTEGER}
	,birthday = #{birthday,jdbcType=TIMESTAMP}
	,married = #{married,jdbcType=INTEGER}
	,wedding_day_calender = #{weddingDayCalender,jdbcType=INTEGER}
	,wedding_day = #{weddingDay,jdbcType=TIMESTAMP}
	,spouse_is_lianer = #{spouseIsLianer,jdbcType=INTEGER}
	,spouse_user_code = #{spouseUserCode,jdbcType=BIGINT}
	,creator = #{creator,jdbcType=BIGINT}
	,creator_name = #{creatorName,jdbcType=VARCHAR}
	,create_time = #{createTime,jdbcType=TIMESTAMP}
	,updater = #{updater,jdbcType=BIGINT}
	,updater_name = #{updaterName,jdbcType=VARCHAR}
	,update_time = #{updateTime,jdbcType=TIMESTAMP}
WHERE id = #{id,jdbcType=BIGINT}

mapper.select:
SELECT

FROM bless_ext_userinfo


		AND id = #{id,jdbcType=BIGINT}


		AND user_code = #{userCode,jdbcType=BIGINT}


		AND org_id = #{orgId,jdbcType=BIGINT}


		AND company_id = #{companyId,jdbcType=BIGINT}


		AND birthday_calender = #{birthdayCalender,jdbcType=INTEGER}


		AND birthday = #{birthday,jdbcType=TIMESTAMP}


		AND married = #{married,jdbcType=INTEGER}


		AND wedding_day_calender = #{weddingDayCalender,jdbcType=INTEGER}


		AND wedding_day = #{weddingDay,jdbcType=TIMESTAMP}


		AND spouse_is_lianer = #{spouseIsLianer,jdbcType=INTEGER}


		AND spouse_user_code = #{spouseUserCode,jdbcType=BIGINT}


