FROM bless_send_target_organization


		AND id = #{id,jdbcType=BIGINT}


ORDER BY id DESC

	LIMIT #{start} ,#{offset}

mapper.update:
UPDATE bless_send_target_organization SET 
	rule_id = #{ruleId,jdbcType=BIGINT}
	,sex = #{sex,jdbcType=INTEGER}
	,age_condition = #{ageCondition,jdbcType=INTEGER}
	,age_value = #{ageValue,jdbcType=INTEGER}
	,org_id = #{orgId,jdbcType=BIGINT}
	,org_name = #{orgName,jdbcType=VARCHAR}
	,include_leave = #{includeLeave,jdbcType=INTEGER}
	,flag_delete = #{flagDelete,jdbcType=INTEGER}
	,creator = #{creator,jdbcType=BIGINT}
	,creator_name = #{creatorName,jdbcType=VARCHAR}
	,create_time = #{createTime,jdbcType=TIMESTAMP}
	,updater = #{updater,jdbcType=BIGINT}
	,updater_name = #{updaterName,jdbcType=VARCHAR}
	,update_time = #{updateTime,jdbcType=TIMESTAMP}
WHERE id = #{id,jdbcType=BIGINT}

mapper.select:
SELECT

FROM bless_send_target_organization


		AND id = #{id,jdbcType=BIGINT}


		AND rule_id = #{ruleId,jdbcType=BIGINT}


		AND sex = #{sex,jdbcType=INTEGER}


		AND age_condition = #{ageCondition,jdbcType=INTEGER}


		AND age_value = #{ageValue,jdbcType=INTEGER}


		AND org_id = #{orgId,jdbcType=BIGINT}


		AND org_name = #{orgName,jdbcType=VARCHAR}


		AND include_leave = #{includeLeave,jdbcType=INTEGER}


		AND flag_delete = #{flagDelete,jdbcType=INTEGER}


		AND creator = #{creator,jdbcType=BIGINT}


		AND creator_name = #{creatorName,jdbcType=VARCHAR}


		AND create_time = #{createTime,jdbcType=TIMESTAMP}


		AND updater = #{updater,jdbcType=BIGINT}


		AND updater_name = #{updaterName,jdbcType=VARCHAR}


		AND update_time = #{updateTime,jdbcType=TIMESTAMP}


ORDER BY id DESC

