mapper.select:
SELECT

FROM b3_solo_archivedate


		AND oId = #{oid,jdbcType=VARCHAR}


		AND archiveDateArticleCount = #{archivedatearticlecount,jdbcType=INT}


		AND archiveDatePublishedArticleCount = #{archivedatepublishedarticlecount,jdbcType=INT}


		AND archiveTime = #{archivetime,jdbcType=BIGINT}


ORDER BY oId DESC

	LIMIT #{start} ,#{offset}

SELECT COUNT(*) FROM b3_solo_archivedate


		AND oId = #{oid,jdbcType=VARCHAR}


		AND archiveDateArticleCount = #{archivedatearticlecount,jdbcType=INT}


		AND archiveDatePublishedArticleCount = #{archivedatepublishedarticlecount,jdbcType=INT}


		AND archiveTime = #{archivetime,jdbcType=BIGINT}
