mapper.select:
SELECT

FROM b3_solo_article


		AND oId = #{oid,jdbcType=VARCHAR}


		AND articleTitle = #{articletitle,jdbcType=VARCHAR}


		AND articleAbstract = #{articleabstract,jdbcType=TEXT}


		AND articleTags = #{articletags,jdbcType=TEXT}


		AND articleAuthorEmail = #{articleauthoremail,jdbcType=VARCHAR}


		AND articleCommentCount = #{articlecommentcount,jdbcType=INT}


		AND articleViewCount = #{articleviewcount,jdbcType=INT}


		AND articleContent = #{articlecontent,jdbcType=MEDIUMTEXT}


		AND articlePermalink = #{articlepermalink,jdbcType=VARCHAR}


		AND articleHadBeenPublished = #{articlehadbeenpublished,jdbcType=CHAR}


		AND articleIsPublished = #{articleispublished,jdbcType=CHAR}


		AND articlePutTop = #{articleputtop,jdbcType=CHAR}


		AND articleCreateDate = #{articlecreatedate,jdbcType=DATETIME}


		AND articleUpdateDate = #{articleupdatedate,jdbcType=DATETIME}


		AND articleRandomDouble = #{articlerandomdouble,jdbcType=DOUBLE}


		AND articleSignId = #{articlesignid,jdbcType=VARCHAR}


		AND articleCommentable = #{articlecommentable,jdbcType=CHAR}


		AND articleViewPwd = #{articleviewpwd,jdbcType=VARCHAR}


		AND articleEditorType = #{articleeditortype,jdbcType=VARCHAR}


ORDER BY oId DESC

	LIMIT #{start} ,#{offset}

SELECT COUNT(*) FROM b3_solo_article


		AND oId = #{oid,jdbcType=VARCHAR}


		AND articleTitle = #{articletitle,jdbcType=VARCHAR}


		AND articleAbstract = #{articleabstract,jdbcType=TEXT}


		AND articleTags = #{articletags,jdbcType=TEXT}


		AND articleAuthorEmail = #{articleauthoremail,jdbcType=VARCHAR}


		AND articleCommentCount = #{articlecommentcount,jdbcType=INT}


		AND articleViewCount = #{articleviewcount,jdbcType=INT}


		AND articleContent = #{articlecontent,jdbcType=MEDIUMTEXT}


		AND articlePermalink = #{articlepermalink,jdbcType=VARCHAR}


		AND articleHadBeenPublished = #{articlehadbeenpublished,jdbcType=CHAR}


		AND articleIsPublished = #{articleispublished,jdbcType=CHAR}


		AND articlePutTop = #{articleputtop,jdbcType=CHAR}


		AND articleCreateDate = #{articlecreatedate,jdbcType=DATETIME}


		AND articleUpdateDate = #{articleupdatedate,jdbcType=DATETIME}


		AND articleRandomDouble = #{articlerandomdouble,jdbcType=DOUBLE}


		AND articleSignId = #{articlesignid,jdbcType=VARCHAR}


		AND articleCommentable = #{articlecommentable,jdbcType=CHAR}


		AND articleViewPwd = #{articleviewpwd,jdbcType=VARCHAR}


		AND articleEditorType = #{articleeditortype,jdbcType=VARCHAR}
