mapper.select:
SELECT

FROM b3_solo_category


		AND oId = #{oid,jdbcType=VARCHAR}


		AND categoryTitle = #{categorytitle,jdbcType=VARCHAR}


		AND categoryURI = #{categoryuri,jdbcType=VARCHAR}


		AND categoryDescription = #{categorydescription,jdbcType=TEXT}


		AND categoryOrder = #{categoryorder,jdbcType=INT}


		AND categoryTagCnt = #{categorytagcnt,jdbcType=INT}


ORDER BY oId DESC

	LIMIT #{start} ,#{offset}

SELECT COUNT(*) FROM b3_solo_category


		AND oId = #{oid,jdbcType=VARCHAR}


		AND categoryTitle = #{categorytitle,jdbcType=VARCHAR}


		AND categoryURI = #{categoryuri,jdbcType=VARCHAR}


		AND categoryDescription = #{categorydescription,jdbcType=TEXT}


		AND categoryOrder = #{categoryorder,jdbcType=INT}


		AND categoryTagCnt = #{categorytagcnt,jdbcType=INT}
