SELECT

FROM b3_solo_category_tag


		AND oId = #{oid,jdbcType=VARCHAR}


		AND category_oId = #{categoryOid,jdbcType=VARCHAR}


		AND tag_oId = #{tagOid,jdbcType=VARCHAR}


ORDER BY oId DESC

	LIMIT #{start} ,#{offset}

SELECT COUNT(*) FROM b3_solo_category_tag


		AND oId = #{oid,jdbcType=VARCHAR}


		AND category_oId = #{categoryOid,jdbcType=VARCHAR}


		AND tag_oId = #{tagOid,jdbcType=VARCHAR}
