mapper.select:
SELECT

FROM b3_solo_link


		AND oId = #{oid,jdbcType=VARCHAR}


		AND linkAddress = #{linkaddress,jdbcType=VARCHAR}


		AND linkDescription = #{linkdescription,jdbcType=VARCHAR}


		AND linkOrder = #{linkorder,jdbcType=INT}


		AND linkTitle = #{linktitle,jdbcType=VARCHAR}


ORDER BY oId DESC

	LIMIT #{start} ,#{offset}

SELECT COUNT(*) FROM b3_solo_link


		AND oId = #{oid,jdbcType=VARCHAR}


		AND linkAddress = #{linkaddress,jdbcType=VARCHAR}


		AND linkDescription = #{linkdescription,jdbcType=VARCHAR}


		AND linkOrder = #{linkorder,jdbcType=INT}


		AND linkTitle = #{linktitle,jdbcType=VARCHAR}
