mapper.select:
SELECT

FROM b3_solo_tag


		AND oId = #{oid,jdbcType=VARCHAR}


		AND tagPublishedRefCount = #{tagpublishedrefcount,jdbcType=INT}


		AND tagReferenceCount = #{tagreferencecount,jdbcType=INT}


		AND tagTitle = #{tagtitle,jdbcType=VARCHAR}


ORDER BY oId DESC

	LIMIT #{start} ,#{offset}

SELECT COUNT(*) FROM b3_solo_tag


		AND oId = #{oid,jdbcType=VARCHAR}


		AND tagPublishedRefCount = #{tagpublishedrefcount,jdbcType=INT}


		AND tagReferenceCount = #{tagreferencecount,jdbcType=INT}


		AND tagTitle = #{tagtitle,jdbcType=VARCHAR}
