mapper.select:
SELECT

FROM b3_solo_user


		AND oId = #{oid,jdbcType=VARCHAR}


		AND userEmail = #{useremail,jdbcType=VARCHAR}


		AND userName = #{username,jdbcType=VARCHAR}


		AND userURL = #{userurl,jdbcType=VARCHAR}


		AND userPassword = #{userpassword,jdbcType=VARCHAR}


		AND userRole = #{userrole,jdbcType=VARCHAR}


		AND userArticleCount = #{userarticlecount,jdbcType=INT}


		AND userPublishedArticleCount = #{userpublishedarticlecount,jdbcType=INT}


		AND userAvatar = #{useravatar,jdbcType=VARCHAR}


ORDER BY oId DESC

	LIMIT #{start} ,#{offset}

SELECT COUNT(*) FROM b3_solo_user


		AND oId = #{oid,jdbcType=VARCHAR}


		AND userEmail = #{useremail,jdbcType=VARCHAR}


		AND userName = #{username,jdbcType=VARCHAR}


		AND userURL = #{userurl,jdbcType=VARCHAR}


		AND userPassword = #{userpassword,jdbcType=VARCHAR}


		AND userRole = #{userrole,jdbcType=VARCHAR}


		AND userArticleCount = #{userarticlecount,jdbcType=INT}


		AND userPublishedArticleCount = #{userpublishedarticlecount,jdbcType=INT}


		AND userAvatar = #{useravatar,jdbcType=VARCHAR}
